assert combine_nested_dictionaries([[['a'], ['x'], [1]], [['b'], ['y'], [2]]]) == {'a': {'x': 1}, 'b': {'y': 2}}
assert combine_nested_dictionaries([[['a'], ['x'], [1]], [['a'], ['x'], [2]]]) == {'a': {'x': 2}}
